mapper.select:
select t.MARKETER_NO, t.MARKETER_NAME, t.DEP_CODE, t1.DEPT_NAME as DEP_NAME, t.PERSONNEL_DIVISION, t.STATUS, t.ENTRY_DATE, t.LEAVE_DATE, t.ALLOCATION_PROPORTION, t.MARKETER_LEVEL, t.CERT_NO, t.TELEPHONE, t.ADDRESS, t.EMAIL, t.REMARK, t.CREATE_TIME, t.UPDATE_TIME, t.CERT_TYPE, t.BIRETHDAY from T_CRM_MARKETER t left join T_CRM_DEPT t1 on t.DEP_CODE = t1.DEPT_CODE
t.MARKETER_NO = #{marketerNo,jdbcType=VARCHAR}
and t.MARKETER_NAME like '%'||#{marketerName,jdbcType=VARCHAR}||'%'
and t.DEP_CODE = #{depCode,jdbcType=VARCHAR}

select t.MARKETER_NO as marketerNo, t.MARKETER_NAME as marketerName, t1.MEDIATOR_NO as mediatorNo, t1.MEDIATOR_NAME as mediatorName, t1.REMARK as remark from T_CRM_MARKETER t, T_CRM_MEDIATOR t1 where t.MARKETER_NO = t1.BELONG_TO and t1.BELONG_TYPE = '1'
and t.MARKETER_NO = #{marketerNo,jdbcType=VARCHAR}
and t.MARKETER_NAME like '%'||#{marketerName,jdbcType=VARCHAR}||'%'
and t.DEP_CODE = #{depCode,jdbcType=VARCHAR}
order by t.MARKETER_NO

select t.MARKETER_NO as marketerNo, t.MARKETER_NAME as marketerName, t1.INVESTOR_NO as investorNo, t1.INVESTOR_NAME as investorName, t1.REMARK as remark from T_CRM_MARKETER t, T_CRM_CUSTOMER t1
t.MARKETER_NO = t1.BELONG_TO and t1.BELONG_TYPE = '1'
and t.MARKETER_NO = #{marketerNo,jdbcType=VARCHAR}
and t.MARKETER_NAME like '%'||#{marketerName,jdbcType=VARCHAR}||'%'
and t.DEP_CODE = #{depCode,jdbcType=VARCHAR}
order by t.MARKETER_NO

select to_char(TO_DATE(MONTH,'yyyymm'),'yyyy-mm') as MONTH , MARKETER_NO , MARKETER_NAME , INTEREST , EXCHANGE_RETURN , INVESTOR_SPECIAL_INTEREST , INVESTOR_SPECIAL_EXCHANGE , MEDIATOR_SPECIAL_INTEREST , MEDIATOR_SPECIAL_EXCHANGE , SXFJSR , DBL16 , MONEY, INTEREST+EXCHANGE_RETURN+SXFJSR-INVESTOR_SPECIAL_INTEREST-INVESTOR_SPECIAL_EXCHANGE-MEDIATOR_SPECIAL_INTEREST-MEDIATOR_SPECIAL_EXCHANGE-DBL16-MONEY as NET_PROFIT from T_REPORT_MARKETER_PROFIT
MONTH >= '201706'
and MARKETER_NO = #{marketerNo}
and MARKETER_NAME = #{marketerName}
order by MONTH desc

select to_char(TO_DATE(MONTH,'yyyymm'),'yyyy-mm') as MONTH , MARKETER_NO , MARKETER_NAME , INTEREST , EXCHANGE_RETURN , INVESTOR_SPECIAL_INTEREST , INVESTOR_SPECIAL_EXCHANGE , MEDIATOR_SPECIAL_INTEREST , MEDIATOR_SPECIAL_EXCHANGE , SXFJSR , DBL16 , MONEY, INTEREST+EXCHANGE_RETURN+SXFJSR-INVESTOR_SPECIAL_INTEREST-INVESTOR_SPECIAL_EXCHANGE-MEDIATOR_SPECIAL_INTEREST-MEDIATOR_SPECIAL_EXCHANGE-DBL16-MONEY as NET_PROFIT from T_REPORT_MARKETER_PROFIT
MONTH >= '201706'
and MARKETER_NO = #{marketerNo}
and MARKETER_NAME = #{marketerName}
order by MONTH desc

mapper.insert:
insert into T_CRM_MARKETER_HISTORY
select * from T_CRM_MARKETER
where MARKETER_NO = #{0}
