mapper.select:
select t.MEDIATOR_NO , t.MEDIATOR_NAME , t.MEDIATOR_SHORT_NAME , t.STATUS , t.DEP_CODE , t1.DEPT_NAME as DEP_NAME, t.ALLOCATION_PROPORTION , t.PAY_TYPE , t.TELEPHONE , t.FAX , t.ADDRESS , t.EMAIL , t.CERT_NO , t.REMARK , t.CREATE_TIME , t.UPDATE_TIME , t.EFFECT_DATE , t.EXPIRE_DATE , t.CERT_TYPE, t.BELONG_TYPE, t.BELONG_TO, t.IS_IB, case t.belong_type when '0' then (select dept_name from t_crm_dept a where a.dept_code = t.belong_to) when '1' then (select b.MARKETER_NAME from t_crm_marketer b where b.marketer_no = t.belong_to) end as BELONG_TO_NAME from T_CRM_MEDIATOR_HISTORY t left join T_CRM_DEPT t1 on t.DEP_CODE = t1.DEPT_CODE
t.MEDIATOR_NO = #{mediatorNo,jdbcType=VARCHAR}
and t.MEDIATOR_NAME like '%'||#{mediatorName,jdbcType=VARCHAR}||'%'
and t.BELONG_TYPE = #{belongType,jdbcType=VARCHAR}
and t.BELONG_TO = #{belongTo,jdbcType=VARCHAR}
and t.DEP_CODE = #{deptCode,jdbcType=VARCHAR}
and t.EXPIRE_DATE >= #{expireBegin,jdbcType=VARCHAR}
and t.EXPIRE_DATE <= #{expireEnd,jdbcType=VARCHAR}

select 
    t.MEDIATOR_NO as mediatorNo,
    t.MEDIATOR_NAME as mediatorName,
    t1.INVESTOR_NO as investorNo,
    t1.INVESTOR_NAME as investorName,
    t1.EFFECT_DATE as effectDate,
    t1.EXPIRE_DATE as expireDate,
    t1.REMARK as remark
from T_CRM_MEDIATOR_HISTORY t, T_CRM_CUSTOMER_HISTORY t1 
   
       t.MEDIATOR_NO = t1.BELONG_TO
       and t1.BELONG_TYPE = '2'
       
           t.MEDIATOR_NO = #{mediatorNo,jdbcType=VARCHAR}
       
       
           and t.MEDIATOR_NAME like '%'||#{mediatorName,jdbcType=VARCHAR}||'%'
       
       
           and t.BELONG_TYPE = #{belongType,jdbcType=VARCHAR}
       
       
           and t.BELONG_TO = #{belongTo,jdbcType=VARCHAR}
       
       
           and t.DEP_CODE = #{deptCode,jdbcType=VARCHAR}
       
       
           and t.EXPIRE_DATE  >=  #{expireBegin,jdbcType=VARCHAR}
       
       
           and t.EXPIRE_DATE  <=  #{expireEnd,jdbcType=VARCHAR}
       
   
order by t.MEDIATOR_NO
