mapper.select:
select 

from T_REPORT_REBATE
where TRADE_DATE_STR = #{0,jdbcType=VARCHAR}
and INVESTOR_NO = #{1,jdbcType=VARCHAR}

  select 
  
  from T_REPORT_REBATE
  
      
          DEPT_NAME like  '%'||#{depName, jdbcType=VARCHAR}||'%'
      
      
          and INVESTOR_NO = #{investorNo, jdbcType=VARCHAR}
      
      
          and INVESTOR_NAME = #{investorName, jdbcType=VARCHAR}
      
      
          and MEDIATOR_NO = #{mediatorNo, jdbcType=VARCHAR}
      
      
          and MARKET_NAME = #{mediatorName, jdbcType=VARCHAR}
      
      
          and to_char(TRADE_DATE,'yyyy-mm-dd') >= #{beginDate}
      
      
          and to_char(TRADE_DATE,'yyyy-mm-dd') <= #{endDate}

select distinct INVESTOR_NO,INVESTOR_NAME,DEPT_NAME 
from T_REPORT_REBATE 
where to_char(TRADE_DATE,'yyyy') = #{0} 
and to_char(TRADE_DATE,'mm') = #{1}
